import { useState } from 'react';
import { NavLink } from 'react-router-dom';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import Box from '@material-ui/core/Box';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import Typography from '@material-ui/core/Typography';
import ListItemText from '@material-ui/core/ListItemText';
import List from '@material-ui/core/List';
import { makeStyles } from '@material-ui/core/styles';
import Logo from './img/Logo.svg';
import Copyright from '@components/Copyright';


const useStyles = makeStyles((theme) => ({
    list: {
        width: 250,
    },
    LogoIcon: {
        width: '50px',
        height: '50px',
    },
    footer: {
        position: "fixed",
        bottom: 0,
        textAlign: "center",
        padding: theme.spacing(2),
    },
}));


function Drawer() {
    const classes = useStyles();
    const [open, setOpen] = useState(false);

    return (
        <>
            <IconButton
                edge="start"
                color="inherit"
                aria-label="open drawer"
                onClick={() => setOpen(true)}
            >
                <MenuIcon />
            </IconButton>

            <SwipeableDrawer
                anchor="left"
                open={open}
                onClose={() => setOpen(false)}
                onOpen={() => setOpen(true)}>
                <div className={classes.list}>
                    <Box textAlign="center" p={2}>
                        <Box className={classes.title}>
                            <img src={Logo} alt="AppLogo" className={classes.LogoIcon} />
                            <Typography variant="h6" noWrap >
                                <strong>SW WIKI</strong>
                            </Typography>
                        </Box>
                    </Box>
                    <Divider />
                    <List>
                        <NavLink to="/" exact style={{ textDecoration: 'none', color: 'inherit' }}>
                            <ListItem button onClick={() => { }}>
                                <ListItemText primary={'Home'}> </ListItemText>
                            </ListItem>
                        </NavLink>

                        <NavLink to="/people/?page=1" style={{ textDecoration: 'none', color: 'inherit' }}>
                            <ListItem button onClick={() => { }}>
                                <ListItemText primary={'Characters'}> </ListItemText>
                            </ListItem>
                        </NavLink>

                        <NavLink to="/planets/?page=1" style={{ textDecoration: 'none', color: 'inherit' }}>
                            <ListItem button onClick={() => { }}>
                                <ListItemText primary={'Planets'}> </ListItemText>
                            </ListItem>
                        </NavLink>

                        <NavLink to="/species/?page=1" style={{ textDecoration: 'none', color: 'inherit' }}>
                            <ListItem button onClick={() => { }}>
                                <ListItemText primary={'Species'}> </ListItemText>
                            </ListItem>
                        </NavLink>

                        <footer className={classes.footer}>
                            <Copyright />
                        </footer>
                    </List>
                </div>
            </SwipeableDrawer>
        </>
    );
}


export default Drawer;
