import { useState } from 'react';
import ErrorMessage from '@components/ErrorMessage/ErrorMessage';


export const withErrorApi = View => {
    return props => {
        const [apiError, setApiError] = useState(false);

        return (
            <>
                {apiError
                    ? <ErrorMessage />
                    : (
                        <>
                            <View
                                setApiError={setApiError}
                                {...props}
                            />
                        </>
                    )
                }
            </>
        )
    }
}
