import PropTypes from 'prop-types';
import { alpha, makeStyles } from '@material-ui/core/styles';
import { useState, useEffect } from 'react';
import { getApiResources } from '@utils/network';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import { withErrorApi } from '@hoc-helpers/withErrorApi';
import PeopleList from '@components/PeoplePage/PeopleList';
import { useQueryParams } from '@hooks/useQueryParams';
import { getPageId } from '@services/getPeopleData';
import PeopleNav from '@components/PeoplePage/PeopleNav';

const useStyles = makeStyles((theme) => ({
    root: {
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    search: {
        margin: theme.spacing(1),
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: alpha(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: alpha(theme.palette.common.white, 0.25),
        },
        marginLeft: 0,
        margin: '10px',
        marginBottom: '30px',
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(1),
            width: 'auto',
        },
    },
    searchIcon: {
        padding: theme.spacing(0, 2),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: '12ch',
            '&:focus': {
                width: '20ch',
            },
        },
    },
}));


function SearchBar({ url, getId, getImg, setApiError }) {
    const classes = useStyles();

    const [inputSearchValue, setInputSearchValue] = useState('');
    const [data, setData] = useState([]);

    // prev && next page 
    const [prev, setPrev] = useState(null);
    const [next, setNext] = useState(null);

    const [counterPage, setCounterPage] = useState(1);

    const query = useQueryParams();
    const queryPage = query.get('page');

    const getResponse = async params => {
        const res = await getApiResources(url + params);

        if (res) {
            const dataList = res.results.map(({ name, url }) => {
                const id = getId(url);
                const img = getImg(id);

                return {
                    id,
                    name,
                    img
                }
            });

            setData(dataList);
            setPrev(res.previous);
            setNext(res.next);
            setCounterPage(getPageId(url));
            setApiError(false);
        } else {
            setApiError(true);
        }
    }

    useEffect(() => {
        getResponse('');
        getResources(API_PEOPLE + queryPage);
    }, []);

    const handleInputChange = (event) => {
        const value = event.target.value

        setInputSearchValue(value);
        getResponse(value);
    }

    return (
        <>

            <div className={classes.search}>
                <div className={classes.searchIcon}>
                    <SearchIcon />
                </div>
                <InputBase
                    placeholder="Searchâ€¦"
                    classes={{
                        root: classes.inputRoot,
                        input: classes.inputInput,
                    }}
                    inputProps={{ 'aria-label': 'search' }}

                    value={inputSearchValue}
                    onChange={handleInputChange}
                />
            </div>

            <PeopleNav
                resource={'people'}
                getResources={getResources}
                prev={prev}
                next={next}
                counterPage={counterPage}
            />

            {data && (
                <PeopleList data={data} link={'people'} />
            )}
        </>
    );
}

SearchBar.propTypes = {
    setApiError: PropTypes.func
}

export default withErrorApi(SearchBar);
