    console.log(url);
    
}



function getId(url, category) {
    const id = url
      .replace(SWAPI_ROOT+category, '')
      .replace(/\//g, '')

    return id;
}

export function getPeopleId(url) {
    return getId(url, SWAPI_PEOPLE);
}

export function getPeopleImg(id) {
    return `${URL_IMG_PERSON}/${id+VGUIDE_IMG_EXTENSION}`;
}
